    public static String calculateExternalId()
    {
        Random r = new Random();
        return "CAR_" + LocalDate.now().getYear() +  r.nextInt(10000);
    }

    public static int calculateSerialNumber()
    {
        Random r = new Random();
        return r.nextInt(999999999);
    }

    public static String calculateLicencePlate()
    {
        Random r = new Random();
        return "AG" + LocalDate.now().getYear() +  r.nextInt(999999);
    }

    public static String calculateCustomerEmail(String customer_type)
    {
        Random r = new Random();
        int _year = LocalDate.now().getYear();
        int _month = LocalDate.now().getMonthValue();
        int _day = LocalDate.now().getDayOfMonth();
        if(customer_type=="natural_person")
        {
            return "german.iturralde+"+_year+"_"+_month+"_"+_day+"_"+r.nextInt(100)+"@wefox.com";
        }else {
            return "german.iturralde+" + _year + "_" + _month + "_" + _day + "_legal_" + r.nextInt(30) + "@wefox.com";
        }

        //return "german.iturralde+italia_CH@wefox.com";
    }
